package jp.co.topgate.sugawara.web;

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.net.ServerSocket;
import java.net.Socket;
import java.net.SocketAddress;

/*
 * This Java source file was generated by the Gradle 'init' task.
 */
public class HttpServer {

    private static final int PORT = 8080;//privateは自分自身のクラスのみアクセスを許可する　staticは静的フィールド（フィールド変数の実体がクラスに準備される）
    public static BufferedReader br = null;

    public static void main(String[] args) throws IOException {//static消したらだめ　getterとかsetterつかう
        System.out.println("start up http server...");

        ServerSocket serverSocket = null;
        Socket socket = null;

        HttpResponse httpResponse= new HttpResponse();
        //cannnot resolve method=メソッドを解決できません @解決方法 HttpResponse(String[]).httpResponseGenerate();を
        //HttpResponse httpResponse= new HttpResponse();にする


        try {
            serverSocket = new ServerSocket(PORT);

            while (true) {
                socket = serverSocket.accept();
                System.out.println("request incoming...");


                //request
                br = new BufferedReader(new InputStreamReader(
                        socket.getInputStream()));
                String str;
                while (!(str = br.readLine()).equals("")) {
                    System.out.println(str);
                }

                //System.out.println(br);


                //response
                //HttpResponseででてきたレスポンスをここにかく

                PrintWriter writer = null;
                writer = new PrintWriter(socket.getOutputStream(),true);
                writer.println(httpResponse.httpResponseGenerate());//httpResponseGenerate(String[]) in HttpResponse cannot be applied@解決方法
            }

        }finally{
            socket.close(); //ソケットをクロースするメソッド
        }

    }
}

